import {useEffect,Fragment, useState} from 'react';
import Header from './components/Layout/Header';
import Meals from './components/Meals/Meals';
import Cart from './components/Cart/Cart';
import CartProvider from './store/CartProvider';
import Button from './components/Layout/Button';
import classes from './components/Meals/AvailableMeals.module.css';

function App() {
  const [pageOneMeals, setOneMeals] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [cartIsShown, setCartIsShown] = useState(false);
  const [pageOnFirst, setChangePage] = useState(true);
  const [httpError, setHttpError] = useState()




    useEffect(() => {
      const fetchMeals = async () => {

       const response = await fetch('https://reacthttp-d29c4-default-rtdb.firebaseio.com/pageOneMeals.json');
       const responseData = await response.json();
       const loadedMeals = [];

       for(const key in responseData){

         loadedMeals.push({
          id: key,
          name: responseData[key].name,
          description: responseData[key].description,
          price: responseData[key].price,
         })
       }
       setIsLoading(false);
       setOneMeals(loadedMeals);

      };
      fetchMeals();

    }, []);



  if(isLoading) {
    return <section className={classes.MealsLoading}>
      <p>Loading...</p>
    </section>
  }





  const pageTwoMeals = [
    {
      id: 'm6',
      name: 'Orange Chicken',
      description: 'Slowly cooked and marinated.',
      price: 15.99,
    },

    {
      id: 'm7',
      name: 'Tuna Sandwich',
      description: 'Always Fresh!',
      price: 7.99,
    },
    {
      id: 'm8',
      name: 'Spicy Jalapeño Omelette',
      description: 'Three eggs, homefries and bacon on the side.',
      price: 14.99,
    },
    {
      id: 'm9',
      name: 'Lobster Tail',
      description: '4 pounds of wild caught lobster tail, chopped.',
      price: 22.99,
    },
    {
      id: 'm10',
      name: 'Chocolate Cake',
      description: 'Topped with strawberries and vanilla frosting.',
      price: 6.99,
    },


  ];



  const showCartHandler = () => {
    setCartIsShown(true);
  };

  const hideCartHandler = () => {
    setCartIsShown(false);
  };

  const forwardPage = () => {
    setChangePage(false);
  }

  const backPage = () => {
    setChangePage(true);
  }

  return (
     <CartProvider>
     {cartIsShown && <Cart onClose={hideCartHandler}/>}
      <Header onCartClick={showCartHandler}/>
      <main>
       {pageOnFirst && <Meals mealsList={pageOneMeals}/>}
       {!pageOnFirst && <Meals mealsList={pageTwoMeals}/>}
       {pageOnFirst && <Button changePage={forwardPage}>Next Page</Button>}
       {!pageOnFirst && <Button changePage={backPage}>Previous Page</Button>}
      </main>


      </CartProvider>
  );
}

export default App;
